import { Button, Card, Col } from "react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';

export function NewsArticle(props) {
    const openFullArticle = () => {
        window.open(props.article.url, '_blank');
    };

    return (
        <Col lg={4}>
            <Card>
                <Card.Img variant="top" src={props.article.urlToImage} />
                <Card.Body>
                    <Card.Title>{props.article.title}</Card.Title>
                    <Card.Text>
                        {props.article.description}
                    </Card.Text>
                    <Button variant="primary" onClick={openFullArticle}>
                        Read full article <FontAwesomeIcon icon={faArrowRight} />
                    </Button>
                </Card.Body>
            </Card>
        </Col>
    );
}
